function Add(number, plus) {
    if(!number) return new Error("[Number] No Number Specified")
    if(isNaN(number)) return new Error("[Number] Number, No Letters Or Symbols")
    if(!plus) return new Error("[Adding] No Adding Number Specified")
    if(isNaN(number)) return new Error("[Adding] Numbers, No Letters Or Symbols")
    return number + plus;
}

module.exports = Add