function Substract(number, substrate) {
    if(!number) return new Error("[Number] No Number Specified")
    if(isNaN(number)) return new Error("[Number] Number, No Letters Or Symbols")
    if(!substrate) return new Error("[Substrate] No Substrat Specified")
    if(isNaN(substrate)) return new Error("[Substrate] Number, No Letters Or Symbols")
    return number - substrate;
}

module.exports = Substract